mapper.select:
SELECT
-- 级联表的字段 ,dianying.dianying_name as dianyingName ,dianying.dianying_zhuyan as dianyingZhuyan ,dianying.dianying_photo as dianyingPhoto ,dianying.leix_types as leixTypes ,dianying.dianying_content as dianyingContent ,yonghu.yonghu_name as yonghuName ,yonghu.sex_types as sexTypes ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_photo as yonghuPhoto
FROM dianying_commentback a left JOIN dianying dianying ON a.dianying_id = dianying.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.dianying_id = #{params.dianyingId}
and a.yonghu_id = #{params.yonghuId}
and DATE_FORMAT(a.insert_time, '%Y-%m-%d %H:%T:%s') >= DATE_FORMAT(#{params.insertTimeStart}, '%Y-%m-%d %H:%T:%s')
and DATE_FORMAT(a.insert_time, '%Y-%m-%d %H:%T:%s') <= DATE_FORMAT(#{params.insertTimeEnd} , '%Y-%m-%d %H:%T:%s')
and a.dianying_commentback_content >= #{params.dianyingCommentbackContentStart}
and a.dianying_commentback_content <= #{params.dianyingCommentbackContentEnd}
and a.commentback_types = #{params.commentbackTypes}
and dianying.dianying_name like CONCAT('%',#{params.dianyingName},'%')
and dianying.dianying_zhuyan like CONCAT('%',#{params.dianyingZhuyan},'%')
and dianying.leix_types = #{params.leixTypes}
and dianying.dianying_content like CONCAT('%',#{params.dianyingContent},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.sex_types = #{params.sexTypes}
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
order by a.${params.orderBy} desc
